<?php$notify = '';$notifyClass = '';if(isset($_POST['submit'])){    // variable    $email = $_POST['email'];    $name = $_POST['name'];     $message = $_POST['message'];    // oke    if(!empty($email) && !empty($name)  && !empty($message)){        if(filter_var($email, FILTER_VALIDATE_EMAIL) === false){            $notify = 'Email Anda salah. Silakan ketikan alamat email yang benar.';            $notifyClass = 'errordiv';        }else{                     $toEmail = 'idcardmu@gmail.com';             $emailSubject = 'Pesan website dari '.$name;            $htmlContent = '<h2>Via Form Kontak Website</h2>                <h4>Nama</h4><p>'.$name.'</p>                <h4>Email</h4><p>'.$email.'</p>                                <h4>Pesan</h4><p>'.$message.'</p>';            // Content-Type header            $headers = "MIME-Version: 1.0" . "\r\n";            $headers .= "Content-type:text/html;charset=UTF-8" . "\r\n";            // Header             $headers .= 'From: '.$name.'<'.$email.'>'. "\r\n";            // Kirim email            if(mail($toEmail,$email,$htmlContent,$headers)){                $notify = 'Pesan Anda sudah terkirim!';                $notifyClass = 'succdiv';            }else{                $notify = 'Maaf pesan Anda gagal terkirim, silahkan ulangi lagi.';                $notifyClass = 'errordiv';            }        }    }else{        $notify = 'Harap mengisi semua field data';        $notifyClass = 'errordiv';    }}?><?xml version="1.0" encoding="UTF-8" ?><!DOCTYPE html><HTML expr:dir='data:blog.languageDirection' lang='en'> <meta charset='utf-8'/> <meta content='width=device-width,initial-scale=1.0,minimum-scale=1.0,maximum-scale=1.0' name='viewport'/>    <title>HUBUNGI - E-MAIL</title>    <link rel="stylesheet" type="text/css" href="style.css"><head><style>@import url(https://fonts.googleapis.com/css?family=Roboto:400,300,600,400italic);* {  margin: 0;  padding: 0;  box-sizing: bo