n: 0 0 5px;  padding: 10px;  font-size: 15px;}  #contact button[type="submit"]:hover {  background: #43A047;  -webkit-transition: background 0.3s ease-in-out;  -moz-transition: background 0.3s ease-in-out;  transition: background-color 0.3s ease-in-out;}  #contact button[type="submit"]:active {  box-shadow: inset 0 0px 0px rgba(0, 0, 0, 0.5);}  .copyright {  text-align: center;}  #contact input:focus,#contact textarea:focus {  outline: 0;  border: 1px solid #aaa;}  ::-webkit-input-placeholder {  color: #888;}  :-moz-placeholder {  color: #888;}  ::-moz-placeholder {  color: #888;}  :-ms-input-placeholder {  color: #888;}  .notify {    padding: 10px;    background-color: #FFD76E;    margin-bottom: 10px;    border-radius: 3px;    font-weight: bold;}</style></head><body>    <div class="container">        <form id="contact" action="<?php $_SERVER['PHP_SELF'] ?>" method="post">            <?php if(!empty($notify)){ ?>                <p class="notify <?php echo !empty($notifyClass)?$notifyClass:''; ?>"><?php echo $notify; ?></p>            <?php } ?>                      <h4>Hubungi kami dengan mengisi kolom dibawah ini dengan benar :</h4>            <fieldset>                <input placeholder="Nama Anda" type="text" name="name" tabindex="1" required autofocus>            </fieldset>            <fieldset>                <input placeholder="Email Anda" type="text" name="email" tabindex="2" required>            </fieldset>            <fieldset>                            </fieldset>            <fieldset>                <textarea placeholder="Ketikan pesan Anda" name="message" tabindex="5" required></textarea>            </fieldset>            <fieldset>                <button name="submit" type="submit" id="contact-submit" data-submit="...Mengirim pesan">Kirim</button>            </fieldset>        </form>    </div>   <script><!--//Disable right click script //var message="Function Disable